#%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%#
##### 2: check the INDEL in the TR region #####
#%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%#


### read data
TR <- system('grep chr16 hg38.v2.bed | grep "CACGTCCAGTGGCTCCCCCGACCTCTGTGATCC"',
             intern = TRUE)
# sample id
TR_head <- system('head -1 hg38.v2.bed',
                  intern = TRUE)
TR_head <- strsplit(TR_head, " ")[[1]][2]
TR_head <- strsplit(TR_head, ";")

## format
TR<-strsplit(TR, "\t")%>%unlist()
names(TR) <- c("chrom",
               "start",
               "end",
               "motif",
               "copy_numbers",
               "sizes",
               "max_change",
               "num_samples",
               "num_calls",
               "motif_frequency",
               "feature")

## subset for length variation
TR_length<-strsplit(TR["sizes"], ";")%>%unlist()
##
names(TR_length) <- unlist(TR_head)
# "-" is missing value
TR_length <- TR_length[TR_length!="-"]
PH01 <- names(TR_length)
TR_length <- round(as.numeric(TR_length))
names(TR_length) <- PH01
# check
length(TR_length)
# [1] 533


### get size of INDEL and allel frequency
table(TR_length)
## Note: 303 is the most common genotype

##
TR_length_diff <- TR_length-303
TR_length_diff_summary <- data.frame(table(TR_length_diff))
TR_length_diff_summary$size_diff <- as.numeric(as.character(TR_length_diff_summary$TR_length_diff))


### plot

## plot the length change
p<-
  TR_length_diff_summary %>%
  filter(size_diff%%33==0&
           size_diff!=0)%>%
  mutate(size_diff=factor(size_diff,levels=sort(unique(size_diff))))%>%
  ggplot(aes(y = size_diff,
             x = Freq
  )) +
  geom_bar(stat = "identity")+
  geom_text(aes(label = Freq),
            hjust = "inward", parse = TRUE, show.legend = FALSE)+
  xlab("Haplotype count")+
  ylab("length change of the TR array (bp)")+
  labs(title = "TR variation in 272 long read-sequenced individuals")+
  theme_linedraw() +
  theme(plot.title = element_text(size = titles + 2, face = 'bold', hjust = 0),
        axis.title = element_text(size = titles),
        axis.text = element_text(size = txt),
        legend.title = element_text(size = titles),
        legend.text = element_text(size = txt),
        legend.position = 'bottom',
        legend.key.size = unit(3, "mm"),
        legend.box.spacing = unit(0.5,"mm"),
        strip.text = element_text(size = txt,
                                  face = 'bold',
                                  margin = margin(0.1,0,0.1,0, "mm"))) +
  guides(color = FALSE)
p
## export
pdf(file = sprintf("%s/INDEL_33bp.v1.pdf",fig_path),
    width = one.c*0.0393701, # The width of the plot in inches
    height = one.c*0.0393701) # The height of the plot in inches
# plot
p
dev.off()

##
my_binwidth <- 1
p<-
as.data.frame(TR_length_diff) %>%
ggplot(aes(x=TR_length_diff)) + 
  geom_histogram(colour="black", fill="white", binwidth = my_binwidth ) +
  geom_density(aes(y=..count..*my_binwidth), alpha=.2, fill="grey") +
  scale_y_break(c(106, 310))+
  scale_y_break(c(55, 95))+
  scale_x_continuous(breaks=seq(-99, 132, 33))+
  scale_y_continuous(breaks=seq(0,320,10))+
  xlab("Change in TR length (bp)")+
  ylab("Number of haplotype")+
  labs(title = "TR variation in 272 long read-sequenced individuals")+
  theme_linedraw() +
  theme(plot.title = element_text(size = titles + 2, face = 'bold', hjust = 0),
        axis.title = element_text(size = titles),
        axis.text = element_text(size = txt),
        legend.title = element_text(size = titles),
        legend.text = element_text(size = txt),
        legend.position = 'bottom',
        legend.key.size = unit(3, "mm"),
        legend.box.spacing = unit(0.5,"mm"),
        strip.text = element_text(size = txt,
                                  face = 'bold',
                                  margin = margin(0.1,0,0.1,0, "mm"))) +
  guides(color = FALSE)
p

## export
pdf(file = sprintf("%s/INDEL_all.v1.pdf",fig_path),
    width = two.c*0.0393701, # The width of the plot in inches
    height = one.c*0.0393701) # The height of the plot in inches
# plot
p
dev.off()




